document.addEventListener('DOMContentLoaded', () => {
  const products = [
      { id: 1, name: 'Zapatilla A', price: 100, image: 'data:image/jpeg;base64,' },
      { id: 2, name: 'Zapatilla B', price: 120, image: 'data:image/jpeg;base64,' },
      { id: 3, name: 'Zapatilla C', price: 150, image: 'data:image/jpeg;base64,' },
  ];

  const productList = document.getElementById('product-list');
  const cartItems = document.getElementById('cart-items');
  const checkoutBtn = document.getElementById('checkout-btn');
  const totalPriceElement = document.getElementById('total-price');

  // Renderiza los productos en la página
  function renderProducts() {
      productList.innerHTML = '';
      products.forEach(product => {
          const productDiv = document.createElement('div');
          productDiv.className = 'product';
          productDiv.innerHTML = `
              <img src="${product.image}" alt="${product.name}" class="product-image">
              <h3>${product.name}</h3>
              <p>$${product.price}</p>
              <button onclick="addToCart(${product.id})">Agregar al carrito</button>
          `;
          productList.appendChild(productDiv);
      });
  }

  // Renderiza los ítems del carrito en la página
  function renderCart() {
      const cart = JSON.parse(localStorage.getItem('cart')) || [];
      cartItems.innerHTML = '';
      let totalPrice = 0;

      cart.forEach(item => {
          const itemLi = document.createElement('li');
          itemLi.textContent = `${item.name} - $${item.price}`;
          cartItems.appendChild(itemLi);
          totalPrice += item.price;
      });

      totalPriceElement.textContent = `Total: $${totalPrice}`;
  }

  // Agrega un producto al carrito
  window.addToCart = function(productId) {
      const product = products.find(p => p.id === productId);
      let cart = JSON.parse(localStorage.getItem('cart')) || [];
      cart.push(product);
      localStorage.setItem('cart', JSON.stringify(cart));
      renderCart();
  };

  // Procesa la compra y limpia el carrito
  function checkout() {
      localStorage.removeItem('cart');
      renderCart();
      alert('Gracias por tu compra!');
  }

  checkoutBtn.addEventListener('click', checkout);

  renderProducts();
  renderCart();
});
